func:
var firstName = msg.req.params.first;
var lastName = msg.req.params.last;
var format = msg.req.query.fmt || 'full';
var title = msg.req.query.title;
function nameFormat(name){
    return name.slice(0,1).toUpperCase() + name.slice(1,name.length).toLowerCase();
}
if(format == 'full'){
    msg.payload = "Hi, " + nameFormat(firstName) +" "+ nameFormat(lastName) +"!";
}else if(format == 'formal'){
    msg.payload = "Hi, "+title+ "." + nameFormat(lastName) +"!";
}else if(format == 'informal'){
    msg.payload = "Hi, " + nameFormat(firstName) +"!";
}
msg.statusCode = 200;
return msg;
